package com.yolt.providers.bunq;

import com.yolt.providers.common.domain.authenticationmeans.BasicAuthenticationMean;
import com.yolt.providers.common.domain.authenticationmeans.TypedAuthenticationMeans;

import java.util.HashMap;
import java.util.Map;
import java.util.UUID;

import static com.yolt.providers.bunq.common.auth.BunqAuthenticationMeansV2.*;


public class AuthMeans {

    public static final String CERTIFICATE_PEM = "-----BEGIN CERTIFICATE-----\n" +
            "\n" +
            "-----END CERTIFICATE-----";
    public static final String CERTIFICATE_CHAIN = "-----BEGIN CERTIFICATE-----\n" +
            "\n" +
            "-----END CERTIFICATE-----\n" +
            "-----BEGIN CERTIFICATE-----\n" +
            "\n" +
            "XH9LrPeeDo87TnEH9HbfBpdl/aGlpmmq4Gtx6EJGJggqPzR8yUr5\n" +
            "-----END CERTIFICATE-----\n" +
            "-----BEGIN CERTIFICATE-----\n" +
            "\n" +
            "-----END CERTIFICATE-----\n";

    public static Map<String, BasicAuthenticationMean> prepareAuthMeansV2() {
        Map<String, BasicAuthenticationMean> authMeans = new HashMap<>();
        authMeans.put(CLIENT_ID, new BasicAuthenticationMean(TypedAuthenticationMeans.CLIENT_ID_STRING.getType(), "aabb"));
        authMeans.put(CLIENT_SECRET_STRING, new BasicAuthenticationMean(TypedAuthenticationMeans.CLIENT_SECRET_STRING.getType(), "bbaa"));
        authMeans.put(PSD2_API_KEY, new BasicAuthenticationMean(TypedAuthenticationMeans.API_KEY_STRING.getType(), "6fa459de8951067c68605df354172298df0c1f06b4737aa7ac5531b1de9fe2eb"));
        authMeans.put(PSD2_USER_ID, new BasicAuthenticationMean(TypedAuthenticationMeans.CLIENT_ID_STRING.getType(), "28196"));
        authMeans.put(OAUTH_USER_ID, new BasicAuthenticationMean(TypedAuthenticationMeans.CLIENT_ID_STRING.getType(), "633"));
        authMeans.put(SIGNING_CERTIFICATE, new BasicAuthenticationMean(TypedAuthenticationMeans.CLIENT_SIGNING_CERTIFICATE_PEM.getType(), CERTIFICATE_PEM));
        authMeans.put(SIGNING_CERTIFICATE_CHAIN, new BasicAuthenticationMean(TypedAuthenticationMeans.CLIENT_SIGNING_CERTIFICATES_CHAIN_PEM.getType(), CERTIFICATE_CHAIN));
        authMeans.put(SIGNING_PRIVATE_KEY_ID, new BasicAuthenticationMean(TypedAuthenticationMeans.KEY_ID.getType(), UUID.randomUUID().toString()));
        return authMeans;
    }
}
